        String evmTx = EvmUtil.getCallEvmEncode(initNFT, "", 0, contractAddress, paraName);
        TransactionAllProtobuf.Transaction tx = TransactionAllProtobuf.Transaction.parseFrom(HexUtil.fromHexString(evmTx)).toBuilder().clearExpire().setChainID(999).setTo(AddressUtil.getToAddress((paraName + "evm").getBytes(), AddressType.ETH_ADDRESS)).build();
        evmTx = HexUtil.toHexString(tx.toByteArray());
//            System.out.println(evmTx);
        long gas = client.queryEVMGas(paraName + "evm", evmTx, managerAddress);
